// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import Navbar from './Navbar';  // Uppdaterad sökväg
import ProductPage from './ProductPage';  // Uppdaterad sökväg
import CartPage from './CartPage';  // Uppdaterad sökväg
import CheckoutPage from './CheckoutPage';  // Uppdaterad sökväg

function App() {
  // State för kundvagnen
  const [cart, setCart] = useState([]);

  // Funktion för att lägga till produkt i kundvagnen
  const addToCart = (productId) => {
    const updatedCart = [...cart, productId];
    setCart(updatedCart);
  };

  // Funktion för att tömma kundvagnen
  const emptyCart = () => {
    setCart([]);
  };

  // Funktion för att slutföra köpet
  const completePurchase = () => {
    // Här kan du lägga till eventuell annan logik för att slutföra köpet
    // Till exempel: skicka en beställning till en backend, uppdatera lagersaldon, etc.

    // Töm kundvagnen när köpet är genomfört
    emptyCart();
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {/* Skicka in cart.length till Navbar för att visa antalet produkter i kundvagnen */}
          <Navbar cartItemCount={cart.length} />
        </header>
        <main>
          {/* Använd Switch för att välja rätt väg baserat på URL */}
          <Switch>
            <Route exact path="/">
              {/* Skicka med addToCart-funktionen till ProductPage */}
              <ProductPage addToCart={addToCart} />
            </Route>
            <Route path="/cart">
              {/* Skicka med cart och emptyCart-funktionen till CartPage */}
              <CartPage cart={cart} emptyCart={emptyCart} checkout={completePurchase} />
            </Route>
            <Route path="/checkout">
              {/* Skicka med cart och completePurchase-funktionen till CheckoutPage */}
              <CheckoutPage cart={cart} completePurchase={completePurchase} />
            </Route>
          </Switch>
        </main>
      </div>
    </Router>
  );
}

export default App;
